COMMENT_PAGE_URL = []


def Get_Url(url):
    g_url = url.split('Page=1')
    for i in range(0, 100):
        COMMENT_PAGE_URL.append(g_url[0] + 'Page=' + str(1 + i) + g_url[1])
    for i in range(0, 100):
        gg_url = g_url[0] + 'Page=' + str(1 + i) + g_url[1]
        gg_url = gg_url.split('append=0')

        COMMENT_PAGE_URL.append(gg_url[0] + 'append=1' + gg_url[1])


